import { doc, getDoc } from 'firebase/firestore';
import { db } from "../firebaseConfig"; 


import React, { useState, useEffect } from 'react';

const PortfolioSection = () => {
  // Simulate selected category state
  const [selectedCategory, setSelectedCategory] = useState('Education');
  const [schoolName, setSchoolName] = useState(''); // For the dynamic school name

  const categories = ['Education', 'School', 'Learn', 'Child', 'Coaching'];

  useEffect(() => {
    const fetchSchoolName = async () => {
      try {
        const docRef = doc(db, "sites", "www.ascentm.in");
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          const siteData = docSnap.data().siteData;

          if (siteData && siteData["home"] && siteData["home"].schoolDetails) {
            setSchoolName(siteData["home"].schoolDetails.schoolName); // Set the school name dynamically
          }
        }
      } catch (error) {
        console.error("Error fetching school name:", error);
      }
    };

    fetchSchoolName();
  }, []);

  const images = [
    { id: 1, src: 'data:image/jpeg;base64,', title: 'Exploring Minds Elementary Best School', subtitle: 'Dreamland Elementary', category: 'Education' },
    { id: 2, src: 'data:image/jpeg;base64,', title: 'Best School for Learning', subtitle: 'Achievers Academy', category: 'School' },
    { id: 3, src: 'data:image/jpeg;base64,', title: 'Child Development Center', subtitle: 'Kiddie Care', category: 'Child' },
    { id: 4, src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSfIwWNHIfz6tgP3sJWEX-9Jh491x_03Ozl9w&s', title: 'Best Learning Programs', subtitle: 'Bright Future Learning', category: 'Learn' },
    { id: 5, src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRiEm--lMeXFqptNQYsXI38VgTTcpwtXB2_PA&s', title: 'Top Coaching Institute', subtitle: 'NextGen Coaching', category: 'Coaching' },
  ];

  // Filter the image based on the selected category
  const filteredImage = images.find(image => image.category === selectedCategory);

  return (
    <div className="bg-white py-10">
      <div className="container mx-auto px-6 lg:px-32">
        <h2 className="text-sm font-semibold text-teal-500 text-center">Latest Portfolio</h2>
        <h1 className="text-3xl font-bold mt-2 mb-6 text-center">
          Exploring Minds {schoolName || 'Elementary School'}
          <br />
          Education the only school
        </h1>
        
        {/* Category Buttons */}
        <div className="flex justify-center space-x-4 mb-8">
          {categories.map((category) => (
            <button
              key={category}
              className={`px-4 py-2 rounded-full font-semibold ${
                selectedCategory === category
                  ? 'bg-orange-500 text-white'
                  : 'bg-white border border-gray-300 text-gray-500 hover:bg-orange-500 hover:text-white'
              }`}
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>
  
        {/* Image Grid */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          {filteredImage && (
            <div className="relative rounded-lg overflow-hidden shadow-lg col-span-2 row-span-2">
              <img src={filteredImage.src} alt={filteredImage.title} className="w-full h-full object-cover" />
              
              {/* Hover Effect */}
              <div className="absolute inset-0 bg-orange-500 bg-opacity-50 flex items-center justify-center p-6">
                <div className="text-white text-center">
                  <h3 className="text-2xl font-bold">{filteredImage.title}</h3>
                  <p className="mt-2">{filteredImage.subtitle}</p>
                </div>
              </div>
            </div>
          )}
  
          {/* Remaining Images */}
          {images
            .filter(image => image.category !== selectedCategory)
            .map(image => (
              <div key={image.id} className="relative rounded-lg overflow-hidden shadow-lg">
                <img src={image.src} alt={image.title} className="w-full h-full object-cover" />
              </div>
            ))}
        </div>
      </div>
    </div>
  );
  
};

export default PortfolioSection;
